-- Update Kunststof Schuifpuien content (keep the same slug)
UPDATE public.products 
SET 
  hero_title = 'Kunststof schuifpuien op maat',
  hero_description = 'Creëer een naadloze overgang tussen binnen en buiten met onze hoogwaardige kunststof schuifpuien.',
  introduction_title = 'Waarom kiezen voor kunststof schuifpuien?',
  introduction_content = '["Kunststof schuifpuien zijn de perfecte oplossing voor wie houdt van veel licht en een open gevoel in huis. Met grote glaspanelen creëert u een naadloze verbinding tussen uw interieur en tuin.", "Onze schuifpuien combineren moderne technologie met tijdloos design, waardoor u jarenlang kunt genieten van comfort, veiligheid en energiebesparing."]'::json,
  what_are_title = 'Wat zijn kunststof schuifpuien?',
  what_are_content = '["Kunststof schuifpuien zijn grote glaspartijen die bestaan uit meerdere panelen die horizontaal langs elkaar schuiven. Ze worden vaak gebruikt als terrasdeuren en bieden een spectaculair uitzicht naar buiten.", "Door de grote glasoppervlakken en slanke profielen krijgt u maximaal licht binnen en een gevoel van ruimte. De schuiffunctie maakt het mogelijk om grote openingen te creëren zonder dat deuren naar binnen of buiten openslaan."]'::json,
  benefits_title = 'Voordelen van kunststof schuifpuien',
  benefits_description = 'Ontdek waarom kunststof schuifpuien de perfecte keuze zijn voor uw woning.',
  benefits_main_content = '["Maximaal licht: Door de grote glasoppervlakken krijgt u veel natuurlijk licht binnen, waardoor uw woonruimte groter en vriendelijker aanvoelt.", "Ruimtebesparend: Schuifpuien nemen geen zwenkruimte in beslag, waardoor u optimaal gebruik kunt maken van uw leefruimte.", "Uitstekende isolatie: Moderne schuifpuien zijn voorzien van hoogwaardige isolatie en HR+++ beglazing voor optimaal wooncomfort.", "Veiligheid: Standaard voorzien van meerpuntssluitingen en veiligheidsglas voor maximale beveiliging.", "Onderhoudsarm: Kunststof profielen behouden jarenlang hun uitstraling zonder schilderen of behandelen."]'::json,
  options_title = 'Schuifpui configuraties',
  options_description = 'Verschillende opstellingen voor elke situatie.',
  options_categories = '[{"title": "2-delig systeem", "items": ["1 vast paneel + 1 schuifpaneel", "Geschikt voor kleinere openingen", "Economische oplossing", "Minimale onderhoudskosten"]}, {"title": "3-delig systeem", "items": ["2 schuifpanelen + 1 vast paneel", "Standaard configuratie", "Goede prijs-kwaliteit verhouding", "Veel variatiemogelijkheden"]}, {"title": "4-delig systeem", "items": ["Meerdere schuifpanelen mogelijk", "Maximale opening mogelijk", "Luxe uitstraling", "Op maat gemaakt"]}, {"title": "Lift & Slide", "items": ["Premium schuifsysteem", "Zeer soepel schuiven", "Extra grote panelen mogelijk", "Topkwaliteit hardware"]}]'::json,
  services_items = '[{"title": "Maatwerk schuifpuien", "description": "Elke schuifpui wordt op maat gemaakt voor uw specifieke situatie", "image": "/lovable-uploads/c5500638-e554-4499-8490-7c52a4ec2a55.png", "features": ["Op maat gemaakt", "Verschillende kleuren", "Diverse glasopties", "Professionele montage"], "linkText": "Meer over maatwerk", "linkUrl": "/schuifpuien/maatwerk"}, {"title": "Schuifpui renovatie", "description": "Vervanging van oude schuifpuien voor beter comfort en uitstraling", "image": "/lovable-uploads/c5500638-e554-4499-8490-7c52a4ec2a55.png", "features": ["Demontage oude pui", "Nieuwe kozijnen", "Professionele afwerking", "Minimale overlast"], "linkText": "Meer over renovatie", "linkUrl": "/schuifpuien/renovatie"}]'::json,
  information_tabs = '[{"title": "Profielen", "content": "Onze schuifpuien worden uitgevoerd in hoogwaardige kunststof profielen met meerkamer systeem voor optimale isolatie. Verkrijgbaar in wit en verschillende houtdecors."}, {"title": "Beglazing", "content": "Standaard voorzien van HR+++ beglazing (triple glas) voor maximale isolatie. Ook verkrijgbaar met zonwering glas, veiligheidsglas of gelaagd glas voor extra veiligheid."}, {"title": "Hardware", "content": "Hoogwaardige schuifhardware van topmerken zoals Siegenia en Roto. Verkrijgbaar in verschillende kleuren en uitvoeringen, van standaard tot lift & slide systemen."}, {"title": "Onderhoud", "content": "Kunststof schuifpuien zijn zeer onderhoudsarm. Regelmatige reiniging van glas en profielen en smering van de hardware is voldoende om jarenlang soepel te blijven functioneren."}]'::json,
  faq = '[{"question": "Wat is het voordeel van een schuifpui ten opzichte van openslaande deuren?", "answer": "Schuifpuien nemen geen zwenkruimte in beslag en kunnen veel bredere openingen creëren. Ook krijgt u meer glas en daarmee meer licht binnen."}, {"question": "Kunnen schuifpuien goed worden afgesloten?", "answer": "Ja, moderne schuifpuien zijn voorzien van meerpuntssluitingen die net zo veilig zijn als normale deuren. Ook zijn ze verkrijgbaar met RC2 inbraakwerend glas."}, {"question": "Hoeveel onderhoud heeft een kunststof schuifpui nodig?", "answer": "Zeer weinig. Regelmatige reiniging en af en toe de hardware smeren is voldoende. De kunststof profielen hoeven nooit geschilderd te worden."}, {"question": "Wat zijn de kosten voor een kunststof schuifpui?", "answer": "De kosten variëren tussen €2.500 en €6.000 afhankelijk van afmetingen, configuratie en opties. Vraag een vrijblijvende offerte aan voor een exacte prijsopgave."}]'::json
WHERE slug = 'kunststof-schuifpuien';

-- Update the product that has slug 'kunststof-deuren' to become proper Kunststof Deuren content
UPDATE public.products 
SET 
  name = 'Kunststof Deuren',
  description = 'Hoogwaardige kunststof deuren voor optimale veiligheid, isolatie en uitstraling van uw woning.',
  seo_title = 'Kunststof Deuren | Veilig & Energiezuinig | Duurzaam Wonen Nederland',
  seo_description = 'Kunststof deuren van topkwaliteit. ✓ Uitstekende isolatie ✓ Inbraakwerend ✓ Onderhoudsvrij ✓ Vele stijlen ✓ 15 jaar garantie. Vraag vrijblijvend offerte aan!',
  seo_canonical_url = 'https://duurzaamwonen.info/kunststof-deuren',
  hero_badge = 'Deur Specialist',
  hero_title = 'Kunststof deuren van topkwaliteit',
  hero_description = 'Veilige, energiezuinige en onderhoudsvrije kunststof deuren die perfect passen bij uw woning.',
  introduction_title = 'Waarom kiezen voor kunststof deuren?',
  introduction_content = '["Kunststof deuren combineren uitstekende isolatie, hoge veiligheid en duurzaamheid in één product. Ze zijn de perfecte keuze voor wie comfort, veiligheid en onderhoudsgemak belangrijk vindt.", "Met onze ruime keuze aan stijlen, kleuren en beglazing vindt u altijd een deur die perfect past bij de architectuur van uw woning."]'::json,
  what_are_title = 'Wat zijn kunststof deuren?',
  what_are_content = '["Kunststof deuren zijn voordelen gemaakt van hoogwaardig PVC-profiel met meerkamer systeem. Ze bieden uitstekende isolatie, zijn volledig onderhoudsvrij en verkrijgbaar in talloze uitvoeringen.", "Door hun constructie zijn ze uiterst duurzaam en behouden ze jarenlang hun mooie uitstraling zonder dat u ze hoeft te schilderen of anderszins te behandelen."]'::json,
  benefits_title = 'Voordelen van kunststof deuren',
  benefits_description = 'Ontdek waarom kunststof deuren de slimme keuze zijn voor uw woning.',
  benefits_main_content = '["Uitstekende isolatie: Door het meerkamer systeem en de goede afdichting blijft warmte binnen en kou buiten, wat resulteert in lagere energiekosten.", "Hoge veiligheid: Standaard voorzien van meerpuntssluitingen en verkrijgbaar met inbraakwerende beglazing en hardware voor optimale beveiliging.", "Onderhoudsvrij: Kunststof deuren behouden jarenlang hun uitstraling zonder schilderen. Alleen af en toe schoonmaken is voldoende.", "Duurzaam: Een kunststof deur gaat gemakkelijk 30-40 jaar mee en is aan het einde van de levensduur volledig recyclebaar.", "Veel keuzemogelijkheden: Verkrijgbaar in vele kleuren, glaspaneel opties en deurstijlen om perfect aan te sluiten bij uw woonstijl."]'::json,
  options_title = 'Deur types en stijlen',
  options_description = 'Van klassiek tot modern, wij hebben de perfecte deur voor uw woning.',
  options_categories = '[{"title": "Voordeur", "items": ["Representatieve uitstraling", "Meerpuntssluitingen", "Inbraakwerende opties", "Diverse glaspanelen"]}, {"title": "Achterdeur", "items": ["Praktische indeling", "Grote glaspartijen mogelijk", "Verschillende openingen", "Optimaal daglicht"]}, {"title": "Tuindeur", "items": ["Openslaand of schuivend", "Lage drempel mogelijk", "Uitzicht op tuin", "Veilige afsluitingen"]}, {"title": "Binnendeur", "items": ["Diverse uitvoeringen", "Geluidsisolatie", "Brandwerende opties", "Stijlvolle afwerking"]}]'::json,
  services_items = '[{"title": "Nieuwe kunststof deuren", "description": "Complete levering en montage van nieuwe kunststof deuren", "image": "/lovable-uploads/f45432a2-b79e-4472-b5b9-daaf325d7017.png", "features": ["Maatwerk mogelijk", "Professionele montage", "Diverse stijlen", "15 jaar garantie"], "linkText": "Meer over nieuwe deuren", "linkUrl": "/kunststof-deuren/nieuw"}, {"title": "Deur renovatie", "description": "Vervanging van oude deuren voor beter comfort en veiligheid", "image": "/lovable-uploads/f45432a2-b79e-4472-b5b9-daaf325d7017.png", "features": ["Demontage oude deur", "Kozijn aanpassingen", "Perfecte pasvorm", "Minimale overlast"], "linkText": "Meer over renovatie", "linkUrl": "/kunststof-deuren/renovatie"}]'::json,
  information_tabs = '[{"title": "Veiligheid", "content": "Onze kunststof deuren zijn standaard voorzien van meerpuntssluitingen en kunnen worden uitgerust met inbraakwerende beglazing en hang- en sluitwerk volgens RC2 of RC3 normen."}, {"title": "Isolatie", "content": "Het meerkamer profiel systeem in combinatie met hoogwaardige afdichtingen zorgt voor uitstekende thermische isolatie. Dit resulteert in lager energieverbruik en hoger wooncomfort."}, {"title": "Beglazing", "content": "Verkrijgbaar met diverse beglazingsopties: van decoratief glas tot veiligheidsglas en van gelaagd glas tot HR+++ isolatieglas voor optimale prestaties."}, {"title": "Onderhoud", "content": "Kunststof deuren zijn praktisch onderhoudsvrij. Regelmatige reiniging met water en een mild schoonmaakmiddel is voldoende om ze er jarenlang als nieuw uit te laten zien."}]'::json,
  faq = '[{"question": "Hoe veilig zijn kunststof deuren?", "answer": "Zeer veilig. Standaard voorzien van meerpuntssluitingen en verkrijgbaar met inbraakwerende beglazing en hardware. Ze kunnen voldoen aan RC2 en RC3 inbraaknormen."}, {"question": "Gaan kunststof deuren lang mee?", "answer": "Ja, een kwaliteitsvolle kunststof deur gaat gemakkelijk 30-40 jaar mee zonder dat de uitstraling achteruitgaat of onderhoud nodig is."}, {"question": "Welke kleuren zijn beschikbaar?", "answer": "Wij leveren kunststof deuren in wit, diverse RAL kleuren en verschillende houtdecors. Ook tweekleurige uitvoeringen zijn mogelijk."}, {"question": "Wat kosten kunststof deuren?", "answer": "De prijs varieert tussen €800 en €2.500 afhankelijk van afmetingen, uitvoering en opties. Vraag een vrijblijvende offerte aan voor een exacte prijsopgave."}]'::json
WHERE slug = 'kunststof-deuren';

-- Update Gevelbekleding
UPDATE public.products 
SET 
  hero_title = 'Moderne gevelbekleding voor uw woning',
  hero_description = 'Verbeter de uitstraling, isolatie en waarde van uw woning met onze hoogwaardige gevelbekleding.',
  introduction_title = 'Waarom kiezen voor nieuwe gevelbekleding?',
  introduction_content = '["Gevelbekleding is een uitstekende manier om uw woning een moderne uitstraling te geven, de isolatiewaarde te verbeteren en tegelijkertijd onderhoudskosten te besparen.", "Of u nu kiest voor kunststof, hout of composiet gevelbekleding, wij zorgen voor een professionele montage die jarenlang meegaat en uw woning een frisse nieuwe look geeft."]'::json,
  what_are_title = 'Wat is gevelbekleding?',
  what_are_content = '["Gevelbekleding is het buitenmateriaal waarmee de gevel van uw woning wordt afgewerkt. Het beschermt de onderliggende constructie tegen weersinvloeden en bepaalt in grote mate de uitstraling van uw huis.", "Moderne gevelbekleding wordt vaak gecombineerd met extra isolatie, waardoor u niet alleen uw woning opknapt maar ook het wooncomfort verbetert en energiekosten bespaart."]'::json,
  benefits_title = 'Voordelen van nieuwe gevelbekleding',
  benefits_description = 'Ontdek de vele voordelen die gevelbekleding biedt voor uw woning.',
  benefits_main_content = '["Verbeterde isolatie: Door gevelbekleding te combineren met isolatiemateriaal verbetert u de energieprestatie van uw woning aanzienlijk.", "Moderne uitstraling: Geef uw woning een frisse, moderne look die de uitstraling en waarde van uw eigendom verhoogt.", "Onderhoudsarm: Vooral kunststof en composiet gevelbekleding vereist minimaal onderhoud en behoudt jarenlang de mooie uitstraling.", "Weerbescherming: Goede gevelbekleding beschermt uw woning tegen regen, wind, vorst en UV-straling.", "Geluidsisolatie: Extra isolatie achter de gevelbekleding zorgt ook voor betere geluidsisolatie van buitengeluiden."]'::json,
  options_title = 'Gevelbekleding materialen',
